 * @param url ä»»æ„ä¸€ä¸ªæ­£å¸¸å®Œæ•´çš„Url,è‡ªåŠ¨æå–æ ¹
 * @returns {string}
 */
function getHome(url) {
    if (!url) {
        return ''
    }
    let tmp = url.split('//');
    url = tmp[0] + '//' + tmp[1].split('/')[0];
    try {
        url = decodeURIComponent(url);
    } catch (e) {
    }
    return url
}

/**
 * getå‚æ•°ç¼–è¯‘é“¾æ¥,ç±»ä¼¼python paramså­—å…¸è‡ªåŠ¨æ‹¼æ¥
 * @param url è®¿é—®é“¾æ¥
 * @param obj å‚æ•°å­—å…¸
 * @returns {*}
 */
function buildUrl(url, obj) {
    obj = obj || {};
    if (url.indexOf('?') < 0) {
        url += '?'
    }
    let param_list = [];
    let keys = Object.keys(obj);
    keys.forEach(it => {
        param_list.push(it + '=' + obj[it])
    });
    let prs = param_list.join('&');
    if (keys.length > 0 && !url.endsWith('?')) {
        url += '&'
    }
    url += prs;
    return url
}

