/**
 * å¿«æ·è·å–ç‰¹æ®Šåœ°å€cookie|ä¸€èˆ¬ç”¨ä½œæœç´¢è¿‡éªŒè¯
 * ç”¨æ³• let {cookie,html} = reqCookie(url);
 * @param url èƒ½è¿”å›cookieçš„åœ°å€
 * @param obj å¸¸è§„è¯·æ±‚å‚æ•°
 * @param all_cookie è¿”å›å…¨éƒ¨cookie.é»˜è®¤falseåªè¿”å›ç¬¬ä¸€ä¸ª,ä¸€èˆ¬æ˜¯PhpSessionId
 * @returns {{cookie: string, html: (*|string|DocumentFragment)}}
 */
function reqCookie(url, obj, all_cookie) {
    obj = obj || {};
    obj.withHeaders = true;
    all_cookie = all_cookie || false;
    let html = request(url, obj);
    let json = JSON.parse(html);
    let setCk = Object.keys(json).find(it => it.toLowerCase() === 'set-cookie');
    let cookie = setCk ? json[setCk] : '';
    if (Array.isArray(cookie)) {
        cookie = cookie.join(';')
    }
    if (!all_cookie) {
        cookie = cookie.split(';')[0];
    }
    html = json.body;
    return {
        cookie,
        html
    }
}

