                        }
                        let list = _pdfa(html, p0.replace('json:', ''));
                        if (list && list.length > 0) {
                            classes = list;
                        }
                    }
                } catch (e) {
                    console.log(e.message);
                }
            } else if (p.length >= 3 && !is_json) { // å¯ä»¥ä¸å†™æ­£åˆ™
                try {
                    let html = homeObj.home_html || getHtml(homeObj.MY_URL);
                    if (html) {
                        homeHtmlCache = html;
                        let list = _pdfa(html, p0);
