            let img = json.body;
            // console.log(img);
            let code = OcrApi.classification(img);
            console.log(`ç¬¬${cnt + 1}æ¬¡éªŒè¯ç è¯†åˆ«ç»“æœ:${code}`);
            let submit_url = `${host}/index.php/ajax/verify_check?type=search&verify=${code}`;
            console.log(submit_url);
            let html = request(submit_url, {headers: {Cookie: cookie}, 'method': 'POST'});
            // console.log(html);
            html = JSON.parse(html);
            if (html.msg === 'ok') {
                console.log(`ç¬¬${cnt + 1}æ¬¡éªŒè¯ç æäº¤æˆåŠŸ`);
                return cookie // éœ€è¦è¿”å›cookie
            } else if (html.msg !== 'ok' && cnt + 1 >= OCR_RETRY) {
                cookie = ''; // éœ€è¦æ¸…ç©ºè¿”å›cookie
            }
        } catch (e) {
