            tab_ordered_list = tab_removed_list.sort((a, b) => {
                return (tab_order.indexOf(a) === -1 ? 9999 : tab_order.indexOf(a)) - (tab_order.indexOf(b) === -1 ? 9999 : tab_order.indexOf(b))
            });
            tab_list = tab_ordered_list;
